import React, { useContext } from "react";
import { Form, Card, ListGroup, FormCheck } from "react-bootstrap";
import { MenuContext } from "./Home";

export default function UpsellListSection({ Name, Details }) {
  // const { handleCheckBox } = useContext(MenuContext);

  function checkItem(change){
    const newDetails= {...Details, ...change}

  }
  return (
    <>
      <Card.Body>
        <Card.Title>{Name}</Card.Title>
        <ListGroup variant="flush">
          {Details.map((i) => {
            return (
              <div>
                <ListGroup.Item>
                  <FormCheck>
                    <FormCheck.Input
                      className="form-check-input"
                      type="checkbox"
                      id={i.id}
                      onChange={e=> checkItem ({isChecked: e.target.checked})}
                    />
                    <FormCheck.Label htmlFor={i.id}>{i.name}</FormCheck.Label>
                  </FormCheck>
                </ListGroup.Item>
              </div>
            );
          })}
        </ListGroup>
      </Card.Body>
      <div class="form-check">
        <input
          class="form-check-input"
          type="radio"
          name="exampleRadios"
          id="exampleRadios1"
          value="option1"
          checked
        />
        <label class="form-check-label" for="exampleRadios1">
          Default radio
        </label>
      </div>
      <div class="form-check">
        <input
          class="form-check-input"
          type="radio"
          name="exampleRadios"
          id="exampleRadios2"
          value="option2"
        />
        <label class="form-check-label" for="exampleRadios2">
          Second default radio
        </label>
      </div>
    </>
  );
}
