import React, { useContext } from "react";
import { Form, Card, ListGroup, FormCheck } from "react-bootstrap";
import { MenuContext } from "./Home";

export default function UpsellListSection({id, Name, Details }) {
  const { handleCheckBox } = useContext(MenuContext);
  return (
    <Card.Body>
      <Card.Title>{Name}</Card.Title>
      <ListGroup variant="flush">
        {Details.map((i) => {
          return (
            <div>
              <ListGroup.Item>
                <FormCheck>
                  <FormCheck.Input
                    type="radio"
                    id={i.id}
                    onChange={() => handleCheckBox(id)}
                  />
                  <FormCheck.Label htmlFor={i.id}>{i.name}</FormCheck.Label>
                </FormCheck>
              </ListGroup.Item>
            </div>
          );
        })}
      </ListGroup>
    </Card.Body>
  );
}
