import React, { useContext } from "react";
import { Form, Card, ListGroup, FormCheck } from "react-bootstrap";
import { MenuContext } from "./Home";

export default function UpsellListSection({ id, Name, Details }) {
  const { handleMenuChange } = useContext(MenuContext);

  function checkItem(id, id2, change) {
    const selectedDetailItem = Details.find((item) => item.id === id);
    const newDetailItem = { ...selectedDetailItem, ...change };
    const newDetails = [...Details];
    newDetails[id] = newDetailItem;
    handleUpsellDetailsChange(id2, newDetails);

    console.log(newDetails);
  }

  return (
    <>
      <Card.Body>
        <Card.Title>{Name}</Card.Title>
        <ListGroup variant="flush">
          {Details.map((i) => {
            return (
              <div>
                <ListGroup.Item>
                  <FormCheck>
                    <FormCheck.Input
                      className="form-check-input"
                      type="checkbox"
                      id={i.id}
                      onChange={(e) =>
                        checkItem(i.id, id, { isChecked: e.target.checked })
                      }
                      // checked={i.isChecked === true}
                    />
                    <FormCheck.Label htmlFor={i.id}>{i.name}</FormCheck.Label>
                  </FormCheck>
                </ListGroup.Item>
              </div>
            );
          })}
        </ListGroup>
      </Card.Body>
      <div class="form-check">
        <input
          class="form-check-input"
          type="radio"
          name="exampleRadios"
          id="exampleRadios1"
          value="option1"
          checked
        />
        <label class="form-check-label" for="exampleRadios1">
          Default radio
        </label>
      </div>
      <div class="form-check">
        <input
          class="form-check-input"
          type="radio"
          name="exampleRadios"
          id="exampleRadios2"
          value="option2"
        />
        <label class="form-check-label" for="exampleRadios2">
          Second default radio
        </label>
      </div>
    </>
  );
}
