import React, { useContext } from "react";
import { Modal, Button, Form, Card, ListGroup } from "react-bootstrap";
import { MenuContext } from "./Home";
import UpsellListSection from "./UpsellListSection";

export default function Upsell({MenuId,  show, UpsellList }) {
  const { handleClose , handleMenuChange} = useContext(MenuContext);

  function handleUpsellDetialsChange(upselldetailsId, Details) {
    const newUpsellDetials = [...UpsellList];
    const UpsellDetailsItem = newUpsellDetials.find((item) => item.id === upselldetailsId);
    UpsellDetailsItem.Details = Details;
    handleMenuChange(MenuId, newUpsellDetials);
  }
  return (
    <div>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Customise Your Food</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* add some styling to saperate the cards.  */}
          <Card style={{ width: "18rem" }}>
            {UpsellList.map((section) => {
              return (
                <UpsellListSection
                  key={section.id}
                  {...section}
                  handleUpsellDetialsChange={handleUpsellDetialsChange()}
                />
              );
            })}
          </Card>

          {/* <Form>
            {["checkbox", "radio"].map((type) => (
              <div key={`default-${type}`} className="mb-3">
                <Form.Check
                  type={type}
                  id={`default-${type}`}
                  label={`default ${type}`}
                />

                <Form.Check
                  disabled
                  type={type}
                  label={`disabled ${type}`}
                  id={`disabled-default-${type}`}
                />
              </div>
            ))}
          </Form> */}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => handleClose()}>
            Close
          </Button>
          <Button variant="primary" onClick={() => handleClose()}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
